package com.day4.loops;

public class EvenNumbersDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// write a program that will print even numbers from 1 to 10
		
		// If the remaining is zero we call it Even number
		
		for(int i = 1; i<=10; i++) {
			
			if (i%2==0)
				
				System.out.print(i + " ");
			
		}
		
		
		
		
		
	}

}
