package com.day5.branchingStatements;

public class Break_in_DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =1;
		do {
			if (i==15) {
				i++;
				break;
			}	
			
			System.out.println(i);
			i++;
		}
		
			while(i<=20);	
				
	}

}		
				
		
			
			
			
			
		
		


