package com.day8.polymorphism;

public class AdditionMainDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		AdditionDemo m1 = new AdditionDemo();
		
		System.out.println(m1.add(10)); //Method Overloading
		System.out.println(m1.add(20, 10, 100)); //Method Overloading
		System.out.println(m1.add(20, 20)); //Method Overloading
	

	}

}
