package com.day9.String;

public class StringBuffer1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// String Buffer is Mutable i.e content can be changed or edit

		// Other methods are similar to String

		// Append(), reverse(), replace(), delete(), substring()

		StringBuffer fname = new StringBuffer("abebe");

		System.out.println(fname.insert(5, " kebede")); // result abebe kebede

		System.out.println(fname); // result abebe kebede

		System.out.println(fname.reverse()); //reverse method

		/* palindrom = examples ... wow , abeba */



	}

}
		
		


