package com.day9.String;

public class StringConcatenate {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Concatenate String

		String fullname = "Ashenafi" + " Moges";

		System.out.println(fullname);

		// To find out String Length
		System.out.println(fullname.length());


		// To print out single character
		System.out.println(fullname.charAt(4));
		System.out.println(fullname.charAt(8)); //it will print only one at the time

	}

}
