package com.day9.String;

public class StringUpperLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// String Upper case and Lower case

		String firstname = "abeba";
		String lastname = "KEBEDE";

		System.out.println(firstname.toUpperCase()); //UPPER CASE

		System.out.println(lastname.toLowerCase()); //Lower case

	}

}
